ee:variables.ee:set-variable:
%dw 2.0
output aplication/java
var search_type = attributes.queryParams.search_type default "="
fun buildQuery() = 
	if (attributes.queryParams.name == null) ""
	else if (attributes.queryParams.search_type == "like") ("and teams.name " ++ search_type ++" " ++ "'%" ++ attributes.queryParams.name ++ "%'")
	else if (attributes.queryParams.search_type == "prefix") ("and teams.name " ++ "like " ++ "'" ++ attributes.queryParams.name ++ "%'")
	else ("and teams.name " ++ "= " ++ "'" ++ attributes.queryParams.name ++ "'")
---
buildQuery()
